import React from 'react'
import $ from 'jquery'


class Splash extends React.Component {
    HideSplash() {    
        $('#JoinNow').fadeOut();
    }
    render() {
        return <div id="JoinNow">
            <div class="IntroTitle">FitPlay</div>
            <div class="IntroImage"><img src="/images/Intro.jpg" /></div>
            <div class="IntroLoginBtn"><a class="button button-round button-fill" onClick={ this.HideSplash.bind(this) }>Login</a></div>
            <div class="IntroSignupBtn">Not registered yet ? <a href="/signup/">Signup</a> now for free.</div>
            <div class="IntroCopyright">Copyright 2022 FitPlay. All Rights Reserved.</div>
        </div>
    }
}

export default Splash;
