import React from 'react'
import $ from 'jquery'


class Splash extends React.Component {
    HideSplash = () => {    
        $('#JoinNow').fadeOut();
    }
    render() {
        return <div id="JoinNow">
            <div class="IntroTitle">User Authentication</div>
            <div class="IntroImage"><span class="material-icons">
lock
</span></div>
            <div class="IntroLoginBtn"><a class="button button-round button-fill" onClick={ HideSplash }>Login</a></div>
            <div class="IntroSignupBtn">Not registered yet ? <a href="/signup/">Signup</a> now for free.</div>
            <div class="IntroCopyright">Copyright 2022 FitPlay. All Rights Reserved.</div>
        </div>
    }
}

export default Splash;
